mapper.select:
/*queryStorageCount*/
SELECT count(1)
FROM
t_category_group_details
WHERE usable_flag = 'USABLE'

    AND CATEGORYID = #{storageDetailDO.categoryId,jdbcType=VARCHAR}

/*queryStoragePage*/
SELECT

FROM
t_category_group_details
WHERE usable_flag = 'USABLE'

  AND CATEGORYID = #{storageDetailDO.categoryId,jdbcType=VARCHAR}

ORDER BY id
LIMIT #{startRow} , #{pageSize}
