    AND storageUrl = #{storageUrl,jdbcType=VARCHAR}


    AND filePath = #{filePath,jdbcType=VARCHAR}


    AND fileName = #{fileName,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=VARCHAR}


